package Entity;/** * @author xmy * @date 2021/3/8 5:31 下午 *///一张表对应一个类public class Student {    private int id;    private String name;    public Student() {    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public String toString() {        return this.id + "," + this.name +",";    }}